val sessionId = scala.util.Random.nextInt(1000000)
val dataPath = s"/deltaoptimizetest/data-$sessionId";

val numFiles = 1000

spark.range(50000000).map { _ =>
    (scala.util.Random.nextInt(10000000).toLong, scala.util.Random.nextInt(1000000000), scala.util.Random.nextInt(2))
}.toDF("colA", "colB", "colC").repartition(numFiles).write.mode("overwrite").format("delta").partitionBy("colC").save(dataPath)

// 50M rows with random integers stored in numFiles * 2 parquet files in colC=0, colC=1 partition

val dt = io.delta.tables.DeltaTable.forPath(dataPath)

// optimize API supports filter condition for partitioned columns. Below will optimize the files under "colC=1" only.
dt.optimize("colC = 1").show
// optimize API returns statistics in Dataframe

